import axios from "axios";

export function getScreenList(domainIp) {
  return new Promise( (resolve) => {
    axios.get(`/edgeReceive${domainIp}/deviceList`,{
      headers: {
        'Access-Control-Allow-Origin': '*',
      }
    })
    .then( (response) => {
      if(response) {
        console.log(response)
        const obj = {}
        obj[domainIp] = {
          data : response.data,
          isEmpty: false,
        } 
        resolve(obj)
      } 
    })
    .catch( () => {
      const obj = {}
        obj[domainIp] = {
          isEmpty: true
        }
      resolve(obj)
      console.error('connection error')
    })
      
  })
}
