import axios from "axios";

export function getScreenList(domainIp) {
  const screenList = []
  return new Promise( (resolve) => {
    axios.get(`http://192.168.22.${domainIp}/edgeReceive/deviceList`)
    .then( (response) => {
      if(response) {
        const obj = {}
        obj[domainIp] = {
          data : response.data,
          isEmpty: false,
        } 
        resolve(obj)
      } 
    })
    .catch( () => {
      screenList.push({ 99 : { isEmpty: true }} )
      console.error('connection error')
    })
      
  })
  
}
