import axios from "axios";

export function getScreenList() {
  const screenList = {}
  return new Promise( (resolve) => {
    axios.get('http://192.168.22.99/edgeReceive/deviceList')
    .then( (response) => {
      console.log(response)
      if(response) {
        screenList[99] = response.data
        resolve()
      } 
    })
    .catch( () => {
      console.error('connection error')
    })
    .then(() => {
      axios.get('http://192.168.22.100/edgeReceive/deviceList')
      .then( (response) => {
        console.log(response)
        if(response) {
          screenList[100] = response.data
          resolve(screenList)
        } 
      })
      .catch( () => {
        console.error('connection error')
      })
    })
      
  })
  
}
